/*
Children - Method for nesting tags into parent tag
*/
func (u *UTagHtml) Children(tags ...any) *UTagHtml {
	return u.supportedChildrenCheck(tags)
}

func (u *UTagHtml) supportedChildrenCheck(tags []any) *UTagHtml {
	for _, tagObj := range tags {
		switch val := tagObj.(type) {
		case string:
			u.registerChildren(TextContentSvg(val).getTag())
		case content:
			u.registerChildren(val.getTag())
		case []content:
			for _, child := range val {
				u.registerChildren(child.getTag())
			}
		default:
			panic(fmt.Sprintf("Unsupported content type. %v", val))
		}
	}
	return u
}

